function diePool(t){this.numPlayers=t,this.allObjects=[],this.generatePool=function(){for(var e=0;5*t>e;e++)this.allObjects.push(new Die(Math.ceil(6*Math.random())))},this.setCurrentCurrentDie=function(t,e){this.allObjects[t]=e},this.removeDie=function(t){t>-1&&this.allObjects.splice(t,1)},this.emptyDiePool=function(){this.allObjects=[]},this.resetDiePool=function(){this.emptyDiePool(),this.generatePool()},this.shuffleDice=function(){for(var t=this.allObjects.length-1;t>0;t--){var e=Math.floor(Math.random()*(t+1)),i=this.allObjects[t];this.allObjects[t]=this.allObjects[e],this.allObjects[e]=i}}}