function Player(e,t,i){this.playerTurnTime=e,this.playerDice=null,this.playerID=i,this.playerNameText=t,this.score=0,this.playerBet={quantity:1,value:1},this.currentState="Playing",this.getDice=function(e){this.playerDice=[];for(var t=diceAssignment[i][0];t<=diceAssignment[i][1];t++)this.playerDice.push(e[t])},this.bet=function(e,t){this.playerBet.quantity=e,this.playerBet.value=t},this.challengePlayer=function(e){console.log("Player "+this.playerNameText+" challenged "+e)},this.changePlayerState=function(e){this.currentState=e},this.loseDice=function(){this.playerDice.splice(0,1)},this.displayHand=function(){for(var e="",t=0;t<this.playerDice.length;t++)e+=" "+this.playerDice[t].value;console.log("The player's hand is: "+e)}}var diceAssignment={0:[0,4],1:[5,9],2:[10,14],3:[15,19]};