function playerPool(t){this.numPlayers=t,this.allObjects=[],this.generatePool=function(){for(var e=0;t>e;e++)this.allObjects.push(new Player(9,"nicu",9))},this.addPlayer=function(e){this.allObjects.length<t&&this.allObjects.push(e)},this.setCurrentCurrentplayer=function(t,e){this.allObjects[t]=e},this.removePlayer=function(t){t>-1&&this.allObjects.splice(t,1)},this.getUserIndexByUserName=function(t){for(var e=0;e<this.allObjects.length;e++)if(this.allObjects[e].playerNameText===t)return e},this.emptyPlayerPool=function(){this.allObjects=[]},this.resetPlayerPool=function(){this.emptyplayerPool(),this.generatePool()}}